  (Name)._Size = (uint16_t)(((uint16_t)sizeof((Name)._Ptr) - ((Align) - 1U)) / (uint16_t)sizeof(Type)); \
  (Name)._Head = 0U; \
  (Name)._Tail = 0U; \
